"use client";

import { motion } from "framer-motion";
import { useInView } from "framer-motion";
import { useRef, useState } from "react";

// Main skills with logos
const firstRowSkills = [
  { name: "React JS", icon: "react" },
  { name: "Nest JS", icon: "nestjs" },
  { name: "Git", icon: "git" },
  { name: "Node JS", icon: "nodejs" },
];

const secondRowSkills = [
  { name: "Express JS", icon: "express" },
  { name: "Laravel", icon: "laravel" },
  { name: "PostgreSQL", icon: "postgresql" },
  { name: "TypeScript", icon: "typescript" },
];

// Other technologies
const otherTechnologies = [
  "HTML5",
  "CSS3",
  "JavaScript",
  "Tailwind CSS",
  "Next.js",
  "RESTful API",
  "Docker",
  "Microservices",
  "DevOps",
];

export default function Skills() {
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true, amount: 0.3 });
  const [hoveredSkill, setHoveredSkill] = useState<string | null>(null);

  return (
    <section id="skills" className="py-20 bg-white overflow-hidden">
      <div className="container mx-auto px-4">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 50 }}
          animate={isInView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 0.5 }}
        >
          <h2 className="text-3xl md:text-4xl font-bold text-center mb-12">
            Technical <span className="text-emerald-600">Skills</span>
          </h2>

          {/* First row - right to left */}
          <div className="relative mb-16 py-8">
            <motion.div
              className="flex gap-16 absolute"
              animate={{
                x: [0, -1000],
              }}
              transition={{
                x: {
                  repeat: Number.POSITIVE_INFINITY,
                  repeatType: "loop",
                  duration: 20,
                  ease: "linear",
                },
              }}
              style={{
                width: "fit-content",
                animationPlayState: hoveredSkill ? "paused" : "running",
              }}
            >
              {[...firstRowSkills, ...firstRowSkills, ...firstRowSkills].map(
                (skill, index) => (
                  <motion.div
                    key={`${skill.name}-${index}`}
                    className="flex flex-col items-center"
                    onMouseEnter={() => setHoveredSkill(skill.name)}
                    onMouseLeave={() => setHoveredSkill(null)}
                    whileHover={{ y: -10, scale: 1.1 }}
                  >
                    <div className="w-24 h-24 mb-3 rounded-xl flex items-center justify-center p-4 bg-white shadow-lg">
                      <TechLogo name={skill.icon} />
                    </div>
                    <p className="text-center font-medium text-gray-700">
                      {skill.name}
                    </p>
                  </motion.div>
                )
              )}
            </motion.div>
          </div>

          {/* Second row - left to right */}
          <div className="relative mb-16 py-8">
            <motion.div
              className="flex gap-16 absolute"
              animate={{
                x: [-1000, 0],
              }}
              transition={{
                x: {
                  repeat: Number.POSITIVE_INFINITY,
                  repeatType: "loop",
                  duration: 20,
                  ease: "linear",
                },
              }}
              style={{
                width: "fit-content",
                animationPlayState: hoveredSkill ? "paused" : "running",
              }}
            >
              {[...secondRowSkills, ...secondRowSkills, ...secondRowSkills].map(
                (skill, index) => (
                  <motion.div
                    key={`${skill.name}-${index}`}
                    className="flex flex-col items-center"
                    onMouseEnter={() => setHoveredSkill(skill.name)}
                    onMouseLeave={() => setHoveredSkill(null)}
                    whileHover={{ y: -10, scale: 1.1 }}
                  >
                    <div className="w-24 h-24 mb-3 rounded-xl flex items-center justify-center p-4 bg-white shadow-lg">
                      <TechLogo name={skill.icon} />
                    </div>
                    <p className="text-center font-medium text-gray-700">
                      {skill.name}
                    </p>
                  </motion.div>
                )
              )}
            </motion.div>
          </div>

          {/* Other technologies */}
          <div className="mt-24 max-w-3xl mx-auto">
            <h3 className="text-2xl font-bold text-center mb-8">
              Other <span className="text-emerald-600">Technologies</span>
            </h3>

            <div className="flex flex-wrap justify-center gap-4">
              {otherTechnologies.map((tech, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, scale: 0.8 }}
                  animate={
                    isInView
                      ? { opacity: 1, scale: 1 }
                      : { opacity: 0, scale: 0.8 }
                  }
                  transition={{ duration: 0.3, delay: index * 0.05 + 0.5 }}
                  whileHover={{
                    scale: 1.05,
                    backgroundColor: "#10b981",
                    color: "white",
                  }}
                  className="px-4 py-2 bg-gray-100 text-gray-800 rounded-full shadow-sm transition-all"
                >
                  {tech}
                </motion.div>
              ))}
            </div>
          </div>
        </motion.div>
      </div>
    </section>
  );
}

